INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (65, '2020-05-03 14:21:05.404712+00', 'Pensjon Pluss AS', 'Pensjon Pluss er et innskuddspensjonsforetak som ble stiftet i april 2013. Det ble tildelt konsesjon fra Finanstilsynet i august 2015 og startet opp virksomheten i august samme år.
