INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (3, '2020-05-03 14:21:05.404712+00', 'EVRY', 'EVRY er et av de ledende IT-selskapene i Norden. Vår ambisjon er å gi mening og liv til informasjon gjennom ideer, teknologi og løsninger som skaper verdier for våre kunder og nytte for samfunnet vi lever i. EVRY kombinerer omfattende bransjekunnskap med en kundenær tilnærming. Vi kombinerer det store foretakets muskler med lokal tilstedeværelse for å hjelpe våre kunder med å realisere sitt fulle IT-potensiale.', '06500', 'careers@evry.com', 'http://evry.no/', NULL, 'UNKNOWN', 'https://github.com/image/76', 'EVRY_3');

