INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (105, '2020-05-03 14:21:05.404712+00', 'Teleplan Globe', 'Med en stab på over 70 medarbeidere, hvor av ca. 50 er programvareingeniører, designer, utvikler og leverer Teleplan Globe programvare og systemløsninger til kunder i bådeinn- og utland.

