INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (18, '2020-05-03 14:21:05.404712+00', 'Polaris Media ASA', 'Polaris Media ASA er børsnotert og ett av Norges ledende medie- og trykkerikonsern med sterke merkevarer som mediehusene Adresseavisen, Romsdals Budstikke, Sunnmørsposten, Harstad Tidende, iTromsø og en rekke mindre lokale mediehus. 

