INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (91, '2020-05-03 14:21:05.404712+00', 'Kodeworks', 'I Kodeworks tror på det å ha en åpen kultur. Skulle det være salgsarbeid, bestemmelser eller lønn. Alle våre betingelser, rutiner og tanker om hvordan ting i Kodeworks fungerer har vi lagt åpen for alle å kunne se. For oss er det folka og felleskapet som utgjør Kodeworks.', NULL, 'jobb@kodeworks.no', 'https://kodeworks.no/', NULL, 'UNKNOWN', 'https://github.com/image/1269', 'Kodeworks_91');

INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (150, '2020-05-03 14:21:05.404712+00', 'Direktoratet for Økonomistyring', 'Direktoratet for økonomistyring forvalter økonomiregelverket og utredningsinstruksen. Dette innebærer å fortolke regelverket, behandle søknader om unntak og svare på spørsmål. Vi formidler kunnskap om hvilke krav regelverkene setter til statlige virksomheter og hva som er god praksis for statlig styring og utredning.

Direktoratet for økonomistyring forvalter og videreutvikler de obligatoriske ordningene for statsregnskapet og statlig betalingsformidling, herunder statens konsernkontoordning. Vi sammenstiller, behandler, kontrollerer og rapporterer oppdaterte regnskapstall for bevilgnings- og kapitalregnskapet. Vi inngår og forvalter rammeavtaler med private banker om betalings- og kontoholdstjenester for statlige virksomheter, samt forvalter konsernkontostrukturen i Norges Bank.

Vi gjennomfører analyser og utredninger som grunnlag for videreutvikling av statlig styring og utredning, og gir råd til Finansdepartementet om forbedringer i regelverk og konsernsystemer.', NULL, NULL, 'https://dfo.no/', NULL, 'UNKNOWN', 'https://github.com/image/641', 'Direktoratet_for_Økonomistyring_150');

