INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (64, '2020-05-03 14:21:05.404712+00', 'Marcello AS', 'Marcello ble stiftet i 2001, har 80 ansatte og en årlig omsetning på ca. 100 millioner kroner. Hovedkontoret ligger i Oslo.

