INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (51, '2020-05-03 14:21:05.404712+00', 'Yahoo Inc.', 'Hovedkontoret ligger i Sunnyvale, California. Utviklingsavdelingen i Trondheim, Yahoo! Technologies Norway, er sentral i utvikling av ny teknologi for bruk i Yahoo!. Yahoo! Technologies Norway har et team på 35 utviklere. Vi lager en plattform for sanntidssøk og prosessering i store datamengder under kontinuerlig endring som er ryggraden i Yahoo!s strategi for å levere de beste og mest innovative løsningene på nett. Denne plattformen brukes i løsninger som takler flere tusen søk per sekund, mange milliarder dokumenter, en milliard brukere, må alltid være oppe og gi svar i løpet av millisekunder. For å håndtere personalisering og maskinlæring i ”big data” ønsker vi utviklere med bakgrunn fra matte/statistikk for å kunne håndtere kravene til ekstrem ytelse. En stor del av presentasjonen vil være faglig!', '', '', 'http://www.yahoo.com', NULL, 'UNKNOWN', 'https://github.com/image/144', 'Yahoo_Inc._51');

