INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (68, '2020-05-03 14:21:05.404712+00', 'Schlumberger', 'With a history and culture of science and innovation, we’re the world’s leading oilfield services company. Our people invent, design, engineer, apply, and maintain technologies that help customers find and produce oil and gas more efficiently and safely—often in remote and challenging locations.

