INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (34, '2020-05-03 14:21:05.404712+00', 'Sogeti', 'Sogeti tilbyr de nyeste løsninger rundt Testing, Business Intelligence & Analytics, Mobile and Cyber Security. Vi kombinerer metoder i verdensklasse og har vår globale leveransemodell, Rightshore®.

