INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (141, '2020-05-03 14:21:05.404712+00', 'Vipps', 'Vi gjør det befriende enkelt å betale eller å identifisere seg. Det er trygt og gjort på et blunk, akkurat slik det skal være.

