INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (145, '2020-05-03 14:21:05.404712+00', 'Orkla', 'Orkla IT arbeider bredt, både geografisk, mot forretningen og med nye teknologiske løsninger. I tillegg sørger vi for implementering av systemer og vi opprettholder stabil drift.
