INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (185, '2020-10-07 21:49:42.461294+00', 'CERN', 'Physicists and engineers at CERN use the world''s largest and most complex scientific instruments to study the basic constituents of matter – fundamental particles. Subatomic particles are made to collide together at close to the speed of light. The process gives us clues about how the particles interact, and provides insights into the fundamental laws of nature. We want to advance the boundaries of human knowledge by delving into the smallest building blocks of our universe.', NULL, NULL, 'https://home.cern/', NULL, 'UNKNOWN', 'https://github.com/image/794', 'CERN_185');

