INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (191, '2021-01-25 13:34:09.220445+00', 'Giant Leap', 'Giant Leap er en av Norges ledende leverandører av løsninger på mobile plattformer. 

