INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (200, '2021-02-23 18:51:08.934629+00', 'Plasto AS', 'Plasto AS, som startet i det små med produksjon av penner i 1955 i Innfjorden, er nå et tredjegenerasjons familie-eid selskap med Lars og Knut Johan Stenerud bak rattet. Dette gjør Plasto AS til et selskap som har sterk lokal tilknytting som samarbeider med mange lokale bedrifter. 

