INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (187, '2020-10-21 09:40:32.841847+00', 'Volue (Old)', 'Volue is an international technology company combining state-of-the-art software, insights and services with deep domain knowledge. We offer solutions and expertise to solve one of the greatest challenges of our time - the massive transition from fossil to renewable energy production.

