INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (204, '2021-04-04 08:18:21.227331+00', 'Knutsen Group', 'Knutsen OAS Shipping AS is a fully integrated shipping company. We do shipping operation, newbuilding supervision, chartering and project development in-house. The head quarter is in Haugesund, Norway.


