INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (213, '2021-05-15 14:38:49.926996+00', 'Massflow', 'Massflow er en digital markedsplass for kjøp og salg av anleggsmasser som jord, stein og grus. Målet vårt er å gjøre det enklere for anleggsbransjen å gjenbruke og sirkulært utnytte overskuddsmassene de sitter igjen med på prosjektene sine, og dermed minimere utslipp i forbindelse med transport og uttak av nye masser. Til høsten sparker vi igang med et pilotprosjekt med kunder og leter nå etter engasjerte utviklere som vil være med på reisen.', NULL, 'sepehr@massflow.no', 'https://massflow.no', NULL, 'UNKNOWN', 'https://github.com/image/924', 'Massflow_213');

