INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (218, '2021-09-02 08:31:53.434868+00', 'Bertel O. Steen', 'Bertel O. Steen AS importerer bilmerkene Mercedes-Benz, Kia, Peugeot, Citroën, DS, Opel, smart®, Setra og Fuso til Norge. Hovedtyngden av salget skjer gjennom vår egeneide forhandlerkjede. Konsernet er også en stor aktør innen bilfinans, verksteddrift og eiendomsvirksomhet.

