INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (104, '2020-05-03 14:21:05.404712+00', 'Progit', 'Progit er et voksende konsulentfirma drevet av innovative og dyktige utviklere. Vi startet opp i 2018, og rekrutteringen foregikk hovedsakelig fra NTNU. I dag er vi totalt 14 ansatte, med én prosjektleder blant utviklerne.

