INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (20, '2020-05-03 14:21:05.404712+00', 'Visma', 'Visma er Nordens ledende leverandør av programvare og tjenester for økonomi og administrasjon, og er kjent som et selskap med finansiell styrke og solid vekst. 

