INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (245, '2022-02-07 21:33:00.057257+00', 'Enernite', 'Our mission is to accelerate tomorrow’s energy solutions through data insight. To reach the EU’s stated goal of 40% renewable energy by 2030, we need to increase renewable energy at least nine-fold from where it is today. Renewable energy requires much larger areas compared to fossil fuels, and without proactive planning, renewable energy could displace extensive areas of wildlife habitat and be prone to natural disasters.
