INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (225, '2021-09-16 09:42:27.587601+00', 'Kvist Solutions', 'Kvist er et raskt voksende selskap, som på kort tid har etablert seg som en vinnende utfordrer i det
