INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (249, '2022-03-07 16:27:33.30587+00', 'Ventistål', 'Ventistål er Norges ledende ventilasjons- og blikkenslagergrossist med ca 300 ansatte fordelt på 20 avdelinger i Norge. Vi tilbyr løsninger innen ventilasjon, varme og kjøling for både bolig og næring, varmepumper, tekniske isolasjonsprodukter, tak og fasade samt tekniske byggautomasjonsprodukter. Ventistål omsetter for ca. 1,5 milliarder inkludert datterselskap. Selskapet er det raskest voksende innen sin bransje i Skandinavia og er 100 % eid av det franske multinasjonale konsernet Saint-Gobain.', '90033013', 'per.nymo@ventistal.no', 'http://www.ventistal.no/', NULL, 'UNKNOWN', 'https://github.com/image/1064', 'Ventistål_249');

INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (250, '2022-03-17 08:46:28.589814+00', 'AboveIT', 'Vi har flere forretningsområder, hvor konsulent i dag står for majoriteten av vår forretning, samtidig som vi jobber med produktutvikling og kan på den måten tilby «the best of both worlds» til våre ansatte. 

