INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (253, '2022-04-20 06:40:01.219445+00', 'Aviant', 'At Aviant, we enable autonomous on-demand transportation of goods using drones. Our vision is to make society more efficient by leveraging autonomous technology. The first step is autonomous transportation of goods. 
