INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (254, '2022-05-13 11:17:02.174364+00', 'Extend', 'Extend AS ble etablert i 1996 og leverer vårt egenutviklede, fleksible web baserte kvalitetssystem-og virksomhetsstyringssystem EQS til over 250 kunder og over 250.000 brukere. EQS er markedsleder i en rekke bransjer og leverer til en rekke spennende kunder, store og små, i privat og offentlig sektor. Selskapet er lokalisert på Leiv Erikson senter i Trondheim og har 12 ansatte som jobber med support, programvareutvikling, markedsføring, salg og rådgivningstjenester. Extend AS er eid av Confirma Software AB og representerer sammen med dem Norges største miljø for kvalitetssystemer og et stort Nordisk systemutviklingsmiljø.', NULL, NULL, 'https://www.extend.no/', NULL, 'UNKNOWN', 'https://github.com/image/1127', 'Extend_254');

INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (251, '2022-03-30 18:40:41.013495+00', 'Spire Consulting', 'Spire Consulting er et studentdrevet konsulentselskap som tilbyr rådgivningstjenester til både små og store bedrifter. Selskapet drives og eies av studenter ved Norges teknisk-naturvitenskapelige universitet (NTNU) i Trondheim. Vår visjon er å gjøre bedrifter i stand til å realisere sitt utviklingspotensial. Motivasjonen bak stiftelsen av Spire Consulting var å skape en arena hvor studenter gis en mulighet til å skape verdi for bedrifter i næringslivet ved bruk av sin kompetanse.', NULL, NULL, 'https://spireconsulting.no/', NULL, 'UNKNOWN', 'https://github.com/image/1100', 'Spire_Consulting_251');

INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (256, '2022-06-06 21:34:33.617729+00', 'WTW', 'Våre egenutviklede produkter innen mobilitet, helsekommunikasjon og betaling skal gi gode brukeropplevelser som folk drar nytte av.

