INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (260, '2022-08-15 09:05:52.649718+00', 'Visma bWise', 'Visma bWise er Norges ledende kompetansesenter innen Business Intelligence, advanced analytics, datavarehus og virksomhetsstyring. I Visma bWise er vi 100 konsulenter som er eksperter innenfor analytics og business intelligence. Vi er et av Norges sterkeste fagmiljø og leverer noen av de største og viktigste prosjektene innen analytics og BI i landet. I Visma bWise hjelper vi våre kunder å gå fra utilgjengelige data til analyse i sanntid. God beslutningsstøtte og avansert analyse har reddet arbeidsplasser i privat sektor, ført til bedre helsetjenester og skapt åpenhet rundt offentlige støtteordninger. I en stadig mer datadrevet verden ser vi et tydelig skifte fra at BI-løsninger blir brukt til rapportering og å se i bakspeilet, til at løsningene blir kritiske i den daglige driften til våre kunder. Mulighetene for også å koble på maskinlæring og prediksjon i analyseverktøyene stiller nye krav til både dataplattform og kompetanse på ulike skyteknologier. Vi skaper innsikt i virksomheter og i samfunnet gjennom avanserte analyser og løsninger.', NULL, NULL, 'https://www.visma.no/business-intelligence/', NULL, 'UNKNOWN', 'https://github.com/image/846', 'Visma_bWise_260');

