INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (262, '2022-08-25 07:44:11.235956+00', 'Dreamknit', 'Vi vil gjøre det mulig for strikkere å skape akkurat det plagget de vil, uten stresset med utregninger og matematikk. Hos oss kan strikkere enkelt velge hvilket design og garn de ønsker plagget i, og kjøpe kvalitetsoppskrifter med grundige forklaringer og videoer. Selv om strikking blitt mer og mer populært den siste tiden, har det skjedd lite med produktene som tilbys strikkere, og markedet er sultent på nye teknologiløsninger og modernisering. Vi i Dreamknit gjør noe med akkurat dette, og har utviklet en webapplikasjon der strikkere kan designe sine egne gensere og kjøpe en digital oppskrift, automatisk generert og sendt på noen sekunder. Vi har nå kunder fra hele verden, og vokser raskt i blant annet Norge, USA, Tyskland og Australia.', NULL, NULL, 'https://www.dreamknit.com', NULL, 'UNKNOWN', 'https://github.com/image/1344', 'Dreamknit_262');

