INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (265, '2022-09-08 08:27:02.709301+00', '321', 'Vi er 321, et ambisiøst IT-konsulentselskap på Hamar – eid av de ansatte. Våre designere og utviklere jobber i spennende oppdrag for noen av Norges største og mest kjente merkevarer.

