INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (109, '2020-05-03 14:21:05.404712+00', 'NorgesGruppen IT og Digitale tjenester', 'Gi teknologikompetansen næring hos NorgesGruppen! 
