INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (80, '2020-05-03 14:21:05.404712+00', 'Norkart', 'Norkart leverer tjenester til alle bransjer som har behov for oppdatert eiendomsinformasjon, og har utviklet eiendomsløsninger for meglere, takstmenn, advokater, finans- og energisektoren.

