INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (277, '2022-11-03 09:40:09.837726+00', 'Blueye Robotics', 'Blueye Robotics is a spin-off from SINTEF Ocean and NTNU AMOS in Trondheim and was founded in 2015 based on the idea to develop technology that creates easier access to what''s below the surface.

