INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (289, '2023-04-19 12:22:20.480829+00', 'Otto', 'Otto ble til fordi vi så at måten folk beveger seg på er i stor endring. Folk som bor på steder med gode sykkel- og kollektivmuligheter er ikke lenger så opptatte av å eie bil. Det går fint an å dele på én. Da slipper man masse stress, det er billigere og ikke minst mer bærekraftig. Visste du f.eks. at én delebil kan erstatte opp til 15 private biler?

