INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (306, '2023-11-22 09:47:33.940821+00', 'Leasi', 'Leasi gir bygg- og anleggsbransjen fullstendig kontroll på maskinparken - enten du er utleier eller entreprenør.  Dette muliggjør bedre utnyttelse av eksisterende og tilgjengelige ressurser som både er økonomisk og miljømessig bærekraftig.

