INSERT INTO company (id, "createdAt", name, description, phone, email, website, location, type, image, slug) VALUES (175, '2020-09-02 19:48:59.010668+00', 'Realfagshjelpen', 'Realfagshjelpen er et privatundervisningsselskap som driver med én-til-én undervisning i realfag for elever og studenter på grunnskole, videregående og universitet i Trondheim, Bergen, Oslo og på nett. Selskapet eies og drives av studenter ved NTNU, og primært indøk. Vår visjon er "Realfagskunnskap videreformidlet av Norges flinkeste studenter". 

