INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (19, NOW(), 22, 'Fast stilling systemutvikler', ' Ciber Norge ansetter hvert år nyutdannede systemutviklere som er på utkikk etter å komme i et solid fagmiljø med fokus på faglig utvikling og kompetansedeling. Vi er stadig på jakt etter dyktige talentfulle kandidater som har et ønske om å bli konsu', '*Ciber Norge er et konsulentselskap innen IT rådgiving, systemutvikling og forretningssystemer og vi leverer prosjekter og tjenester til private og offentlige virksomheter. Vi er 200 ansatte og holder til sentralt Oslo. Vi er opptatt av faglig dyktighet og fornøyde kunder, og vi har et eksepsjonelt godt miljø med et bredt sosialt tilbud. Våre faglige styrker ligger innenfor følgende områder: Rådgivning, strategi og kvalitet | Design og utvikling | Forvaltning og drift |e-commerce og forretningssystemer.*

