INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (21, NOW(), 21, 'Sommerjobb i Cxense', 'Har du lyst til å jobbe med strategisk utvikling på verdensledende produkter? Cxense tilbyr sommerjobb i 6-8 uker fra slutten av juni i vårt utviklingsmiljø i Oslo for studenter som ønsker å bruke sommeren på spennende og krevende problemstillinger i', 'Med sommerjobb i Cxense vil du jobbe sammen med våre faste utviklere på problemstillinger og utviklingsoppgaver som er viktige for oss og våre produkter.

