INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (159, NOW(), 60, 'Appear TV søker nyutdannede', 'Kunne du tenke deg å jobbe i Appear TV? Vi er på jakt etter nye ansatte!', '### Appear TV is looking for Java Developers and Software Developers

