INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (114, NOW(), 7, 'Graduates for Technology Consulting & Technology - 2017', 'Vi søker nyutdannede til spennende og innovative prosjekter. Hos oss får du jobbe med de nyeste og mest avanserte teknologiene på markedet, du får utvikle nye løsninger og være med på noen av de mest meningsfulle IT-prosjektene i Norge. Måten vi bruk', '**Accenture er et ledende globalt konsulentselskap som leverer tjenester innen teknologi, digitalisering, rådgivning, strategi og outsourcing. Med erfaring, spesialisert kompetanse, globale ressurser og omfattende bransjeekspertise, jobber Accenture i sjiktet mellom business og teknologi for å hjelpe kunder øke sin prestasjonsevne og utvikle bærekraftig verdi. Med 375,000 ansatte og kunder i mer enn 120 land, driver Accenture innovasjon for en forenklet hverdag. For mer informasjon, besøk oss på www.accenture.no**

