INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (32, NOW(), 31, 'Silicon Valley Bootcamp', 'Har du drømt om å jobbe i Silicon Valley? Kunne du tenke deg å jobbe i en startup når du er ferdig med å studere? Nå har du muligheten til å delta på startup-bootcamp i Silicon Valley! ', 'Opplegget arrangeres av etablerte norske gründere i samarbeid med relevante foreninger på gløshaugen og Innovasjon Norge. I to intensive uker får 5-10 ekstremt motiverte utviklere en unik mulighet til å jobbe sammen med gründere som har bygget selskaper i Silicon Valley.

