INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (120, NOW(), 57, 'Nyutdannede systemutviklere  innen Java, Microsoft .NET og  Front-end/mobil', 'Norges sterkeste fagmiljø: Det står vi inne for. Ja, det kan jo en hvilken som helst større aktør si,  vil du sikkert tenke. Du er jo ikke helt ukritisk heller? Er du, vel?! Bra, da kan du fortsette å lese.', 'Norges sterkeste fagmiljø: Det står vi inne for. Ja, det kan jo en hvilken som helst større aktør si, vil du sikkert tenke. Du er jo ikke helt ukritisk heller? Er du, vel?! Bra, da kan du fortsette å lese.

