INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (136, NOW(), 68, 'Summer internship in Silicon Valley for 2017', 'Schlumberger Technology Innovation Center (STIC) is looking for a talented student from NTNU for a summer internship in Silicon Valley for 2017.', 'Schlumberger Technology Innovation Center (STIC) is looking for a talented student from NTNU for a summer internship in Silicon Valley for 2017.  

Areas of interest include: Big Data, UX, IoT, Data Analytics, HPC, Frontend (Web/Mobile), Machine Learning, AI, Security, Cloud and Virtual Reality.

