INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (74, NOW(), 53, 'Sommerstipend hos Blank', 'Blank søker 2 studenter til sitt sommerstipend! ', 'Som et alternativ til en tradisjonell sommerjobb ønsker vi å tilby inntil to studenter et “sommerstipend”, slik at disse kan jobbe med et prosjekt de velger selv.
