INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (54, NOW(), 5, 'Nyutdannede 2016', 'Som nyutdannet i Capgemini får du en god start gjennom vårt 18 måneders graduate program, Technology Analysis Group (TAG). Her får du tilrettelagt kurstilbud, variert og krevende prosjekterfaring, samt god oppfølging fra første stund.', '**Digital Insights**

