INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (141, NOW(), 6, 'IT-konsulent i traineeprogrammet Nytt Krutt', 'Bli med i digitaliseringen av Norge som Nytt Krutt i Visma Consulting. Gjennom vårt traineeprogram får du en kickstart på karrieren som konsulent, en trygg overgang til arbeidslivet, fast jobb og en vennegjeng for livet.', 'IT-studenter kåret i 2016 Nytt Krutt-programmet til norges 3. mest attraktive traineeprogram for IT-studenter i undersøkelsen Karrierebarrometeret.  

