INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (143, NOW(), 74, 'NTNU IT/Utvikling søker Avdelings-/overingeniør Datavarehus/utvikler', 'Ved utviklingsseksjonen hos NTNU sin IT avdeling er det ledig fast stilling som Datavarehus/BI-utvikler i 100%.', 'Vi søker etter en initiativrik og dyktig medarbeider, gjerne nyutdannet, med interesse for BI og forretningsstyring, som vil inngå i et større tverrfaglig team.

