INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (147, NOW(), 20, 'Software R&D Graduate - oppstart 2018', 'Vismas Software R&D Graduate-program rettes mot deg som ønsker å jobbe med disruptiv teknologi og drive utviklingen av nye tjenester som skal gjøre arbeidshverdagen bedre for millioner av brukere.', 'Vismas Software R&D Graduate-program rettes mot deg som ønsker å jobbe med disruptiv teknologi og drive utviklingen av nye tjenester som skal gjøre arbeidshverdagen bedre for millioner av brukere.

