INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (178, NOW(), 77, 'Graduate Information Technology', 'ConocoPhillips is looking for a graduate with a master degree in Information Technology', '###Our Company
