INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (150, NOW(), 6, 'Vil du kickstarte karrieren som systemutvikler med oppstart i februar?', 'Visma Consulting søker systemutviklere til traineeprogrammet Nytt Krutt for deg som ønsker å starte allerede i februar 2018', 'Visma Consulting søker systemutviklere til traineeprogrammet Nytt Krutt for deg som ønsker å starte allerede i februar 2018.


