INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (91, NOW(), 38, 'Sommerjobb hos Capra Consulting som Scrum Master', 'Dersom du ønsker utfordringer fra dag én som vil akselerere læringskurven langt over det du  tror er mulig burde du lese videre.', 'Dersom du ønsker utfordringer fra dag én som vil akselerere læringskurven langt over det du tror er mulig burde du lese videre.

