INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (98, NOW(), 23, 'Sommerjobb systemutvikler', 'DIPS ASA er den største leverandøren av systemer for elektronisk pasientjournal til sykehusene i Norge. Våre løsninger er markedsledende på funksjonalitet, sikkerhet og brukbarhet. Vi har kontrakter med 85% av sykehusene i Norge.  DIPS er det vikti', '**DIPS ASA er et solid programvarehus i kraftig vekst. Vi er 270 dyktige medarbeidere, fordelt på hovedkontoret i Bodø og avdelingskontorer i Trondheim, Tromsø, Oslo, Bergen og Stockholm. Vi ønsker å effektivisere helsevesenet ved å lage en fantastisk elektronisk pasientjournal utviklet av dyktige utviklere i samarbeid med en erfaren medisinsk stab.**

